etalert2@11"></script>    <script>      function simpan_transaksi() {        let id_detail         = $("#id_detail").val();        let jumlah_perangkat  = $("#jumlah_perangkat").val();        //let perangkat         = $('#id_perangkat:checked').val();        let jlh_item = [];        $("input:checkbox[name=id_perangkat]:checked").each(function() {          jlh_item.push($(this).val());        });        console.log(jlh_item);        if (jlh_item.length > 0) {          $.ajax({            url: "<?php echo base_url(); ?>perawatan/perawatan_transaksi_save",            data: {              items: jlh_item,              id_details: id_detail,              jlh_perangkat: jumlah_perangkat            },            type: 'POST',            dataType: 'JSON',            beforeSend: function() {              //$("#loading-screen").show();            },            success: function(hasil) {                          },            error: function(error) {              alert("Error when proses data from controller");            }          })        } else {          alert("Silahkan pilih item dahulu!");        }      }      document.addEventListener("DOMContentLoaded", function(event) {      });    </script>  </body></html>